const TravelPlanner = () => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [showAllCities, setShowAllCities] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedInterests, setSelectedInterests] = useState([]);

  const toggleInterest = (interest) => {
    setSelectedInterests((prev) =>
      prev.includes(interest)
        ? prev.filter((i) => i !== interest)
        : [...prev, interest]
    );
  };

  const filteredOtherCities = otherCities.filter((city) =>
    city.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const tripForm = {
    city: selectedCity,
    interests: selectedInterests,
  };

  const handleNext = () => {
    localStorage.setItem("tripForm", JSON.stringify(tripForm));
    navigate("/places");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white p-6 font-sans">
      <h2 className="text-3xl text-center text-white font-bold mb-4 flex justify-center items-center gap-2">
        <FaMapMarkedAlt className="text-pink-500" />
        Select Your Destination
      </h2>
      <div className="max-w-5xl mx-auto">
        <AnimatePresence>
          {step === 1 && (
            <motion.div
              key="city-selection"
              initial={{ opacity: 0, y: 40 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -40 }}
              transition={{ duration: 0.5 }}
            >
              <input
                type="text"
                placeholder="Search for your city"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full border border-gray-300 rounded-md px-4 py-2 mb-6 focus:outline-none focus:ring-2 focus:ring-pink-500 text-white"
              />

              <h2 className="text-lg font-semibold mb-4">Popular Cities</h2>
              <div className="grid grid-cols-2 sm:grid-cols-5 gap-4 mb-8">
                {Object.entries(popularCities).map(
                  ([city, imageUrl], index) => (
                    <div
                      key={index}
                      onClick={() => {
                        setSelectedCity(city);
                        setStep(2);
                      }}
                      className="cursor-pointer  text-white rounded-lg shadow p-4 text-center hover:bg-gray-700 transition"
                    >
                      <img
                        src={imageUrl}
                        alt={city}
                        className="mx-auto mb-2 rounded-3xl w-90 h-40 object-cover"
                      />
                      {city}
                    </div>
                  )
                )}
              </div>

              <h2 className="text-lg font-semibold mb-4">Other Cities</h2>
              <div className="grid grid-cols-2 items-center justify-center text-center sm:grid-cols-4 gap-3 text-gray-200 text-sm mb-4">
                {(showAllCities
                  ? filteredOtherCities
                  : filteredOtherCities.slice(0, 16)
                ).map((city, index) => (
                  <span
                    key={index}
                    className="cursor-pointer hover:underline"
                    onClick={() => {
                      setSelectedCity(city);
                      setStep(2);
                    }}
                  >
                    {city}
                  </span>
                ))}
              </div>

              {filteredOtherCities.length > 16 && (
                <button
                  className="text-pink-600 font-medium hover:underline mt-2"
                  onClick={() => setShowAllCities(!showAllCities)}
                >
                  {showAllCities ? "Hide All Cities" : "Show All Cities"}
                </button>
              )}
            </motion.div>
          )}
        </AnimatePresence>

        <AnimatePresence>
          {step === 2 && (
            <motion.div
              key="interest-selection"
              initial={{ opacity: 0, y: 40 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -40 }}
              transition={{ duration: 0.6 }}
              className="mt-8 space-y-6"
            >
              <h2 className="text-2xl font-bold text-center">
                Select Your Interests in {selectedCity}
              </h2>

              <div className="grid grid-cols-2 sm:grid-cols-3 gap-4">
                {interests.map((interest) => (
                  <div
                    key={interest.name}
                    onClick={() => toggleInterest(interest.name)}
                    className={`relative rounded-xl overflow-hidden cursor-pointer group ${
                      selectedInterests.includes(interest.name)
                        ? "ring-4 ring-purple-500"
                        : ""
                    }`}
                  >
                    <img
                      src={interest.image}
                      alt={interest.name}
                      className="w-full h-60 object-cover group-hover:scale-105 transition"
                    />
                    <div className="absolute bottom-0 left-0 right-0 bg-black bg-opacity-60 text-center py-2">
                      {interest.name}
                    </div>
                  </div>
                ))}
              </div>

              {selectedInterests.length > 0 && (
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  className="block w-full mt-10 py-3 rounded-xl bg-purple-700 text-white text-lg font-semibold shadow-lg hover:bg-purple-800 transition"
                  onClick={handleNext}
                >
                  Plan My Trip
                </motion.button>
              )}
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default TravelPlanner;
